import React, { useState } from 'react';
import trashIcon from '../assets/img/trash.png';
import ChengeIcon from '../assets/img/ChengeIcon.png';
import { CommentType } from '../app/entity';
import {
  ImgTheme,
  ImgText,
  ButtonChenge,
  InputComment,
  ButtonComment,
  CommentWrapper,
  ButtonTrash,
  CommentHeader,
} from './commentStyling';
function Comment(props: {
  comment: CommentType;
  newTextComment(text: string, id: number): void;
  onDeleteCommets(id: number): void;
  idCommentChenge: number;
  setIdCommentChenge(idCommentChenge: number): void;
}) {
  const [valueComment, setValueComment] = useState('');
  const [chenge, setChenge] = useState(false);
  function onValueComment(e: React.FormEvent<HTMLInputElement>): void {
    setValueComment(e.currentTarget.value);
  }
  function newText() {
    props.newTextComment(valueComment, props.comment.idComents);
    setChenge(false);
    props.setIdCommentChenge(-1);
  }
  function openChenge() {
    setChenge(true);
    props.setIdCommentChenge(props.comment.idComents);
  }
  let statusComment: JSX.Element;
  if (chenge && props.idCommentChenge === props.comment.idComents) {
    statusComment = (
      <div>
        <InputComment
          type="text"
          defaultValue={props.comment.coment}
          onChange={onValueComment}
        />
        <ButtonComment onClick={() => newText()}>Изменить</ButtonComment>
      </div>
    );
  } else {
    statusComment = (
      <div>
        <span>{props.comment.coment}</span>
        <ButtonChenge onClick={() => setChenge(true)}>
          <ImgText src={ChengeIcon} alt="" />
        </ButtonChenge>
      </div>
    );
  }
  return (
    <CommentWrapper key={props.comment.idComents}>
      <CommentHeader>
        <p>Автор: {props.comment.authorComents}</p>
        <ButtonTrash
          onClick={() => props.onDeleteCommets(props.comment.idComents)}>
          <ImgTheme src={trashIcon} alt="" />
        </ButtonTrash>
      </CommentHeader>
      {statusComment}
    </CommentWrapper>
  );
}
export default Comment;
