import React from 'react';
import trashIcon from '../assets/img/trash.png';
import openIcon from '../assets/img/open.png';
import closeIcon from '../assets/img/close.png';
import { cardProps } from '../app/entity';
import {
  CardWraper,
  ButtonCards,
  ImgCards,
  ButtonWraper,
  ThemeColumn,
  FooterCard,
} from './CardsStyling';
function Cards(props: cardProps) {
  const { theme, author, text, onDelete, onToggleChecked, id } = props;
  let checedIcons: JSX.Element;
  if (props.checked) {
    checedIcons = <ImgCards src={openIcon} alt="" />;
  } else {
    checedIcons = <ImgCards src={closeIcon} alt="" />;
  }
  const CardIndex = props.cards.findIndex((elem) => elem.id === id);
  function openPopup() {
    props.setPopupCard([{ status: true, cardIndex: id }]);
    props.cards[CardIndex].changeText = false;
    props.cards[CardIndex].changeTheme = false;
    for (let i = 0; i < props.comments.length; i++) {
      props.comments[i].chengeComment = false;
    }
    props.setSwitchs(!props.switchs) as void;
  }
  const colComment = [0];
  props.comments.map((item) => {
    if (item.idCards === id) {
      colComment[0] += 1;
    }
  });
  return (
    <CardWraper onClick={() => openPopup()}>
      <div className="card__info-wrapper">
        <ThemeColumn>{theme}</ThemeColumn>
        <p className="card__autor">Автор: {author}</p>
        <div className="card__text-wrapper">
          <span className="card__text">{text}</span>
        </div>
      </div>
      <FooterCard>
        <p>Комментарии:{colComment}</p>
        <ButtonWraper>
          <div onClick={(e) => e.stopPropagation()}>
            <ButtonCards
              className="card__checked-btn card__btn"
              onClick={() => onToggleChecked(id)}>
              {checedIcons}
            </ButtonCards>
          </div>
          <div onClick={(e) => e.stopPropagation()}>
            <ButtonCards onClick={() => onDelete(id)}>
              <ImgCards src={trashIcon} alt="" />
            </ButtonCards>
          </div>
        </ButtonWraper>
      </FooterCard>
    </CardWraper>
  );
}
export default Cards;
